mapper.select:
SELECT * from ( SELECT ddr.id, ddr.project_id, ddr.deploy_type, ddr.deploy_id, ddr.deploy_time, dc.`status` deployStatus, dc.created_by deployCreatedBy, NULL pipelineName, NULL pipelineTriggerType, NULL pipelineId, dasi.id instanceId, dasi.env_id envId, de.name as env, dasi.app_service_id appServiceId FROM devops_deploy_record ddr LEFT JOIN devops_env_command dc ON ddr.deploy_id = dc.id LEFT JOIN devops_app_service_instance dasi ON dc.object_id = dasi.id LEFT JOIN devops_env de on de.id = dasi.env_id WHERE ddr.project_id=#{projectId} and ddr.deploy_type = 'manual'
AND (dc.`status` ='running' OR dc.`status` ='operating')
AND ddr.deploy_type LIKE CONCAT(CONCAT('%', #{searchParam.deployType, jdbcType=VARCHAR}),'%')
AND dc.`status` =#{searchParam.deployStatus}
AND dasi.env_id =#{searchParam.env}
AND ddr.deploy_id =#{searchParam.deployId}
AND
(ddr.deploy_type LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR dc.`status` LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR de.name LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') )
UNION SELECT ddr.id, ddr.project_id, ddr.deploy_type, ddr.deploy_id, ddr.deploy_time, dpc.`status` deployStatus, dpc.created_by deployCreatedBy, dpc.pipeline_name pipelineName, dpc.trigger_type pipelineTriggerType, dpc.pipeline_id pipelineId, NULL instanceId, dpasd.env_id envId, de.name as env, NULL appServiceId FROM devops_deploy_record ddr LEFT JOIN devops_pipeline_record dpc ON ddr.deploy_id = dpc.id JOIN devops_pipeline dp ON dp.id=dpc.pipeline_id JOIN devops_pipeline_stage dps ON dps.pipeline_id=dpc.pipeline_id JOIN devops_pipeline_task dpt ON dpt.stage_id =dps.id JOIN devops_pipeline_app_service_deploy dpasd ON dpasd.id=dpt.app_service_deploy_id JOIN devops_env de on dpasd.env_id = de.id WHERE ddr.project_id = #{projectId} and ddr.deploy_type = 'auto'
AND (dpc.`status` ='running' OR dpc.`status` ='operating')
AND ddr.deploy_type LIKE CONCAT(CONCAT('%', #{searchParam.deployType, jdbcType=VARCHAR}),'%')
AND dpc.`status` =#{searchParam.deployStatus}
AND dpc.pipeline_name LIKE CONCAT(CONCAT('%', #{searchParam.pipelineName, jdbcType=VARCHAR}),'%')
AND dpasd.env_id =#{searchParam.env}
AND ddr.deploy_id =#{searchParam.deployId}
AND dpc.pipeline_id =#{searchParam.pipelineId}
AND
(ddr.deploy_type LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR dpc.`status` LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR dpc.pipeline_name LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR de.name LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') )
) t where t.project_id=#{projectId} ORDER BY t.id desc
